#!/bin/bash\n# QuickQueue GitHub Deployment Script\n\nset -e\n\necho \"ðŸš€ QuickQueue GitHub Deployment Helper\"\necho \"=====================================\"\n\n# Colors for output\nGREEN='\\033[0;32m'\nYELLOW='\\033[1;33m'\nRED='\\033[0;31m'\nNC='\\033[0m' # No Color\n\nprint_success() {\n    echo -e \"${GREEN}âœ… $1${NC}\"\n}\n\nprint_warning() {\n    echo -e \"${YELLOW}âš ï¸  $1${NC}\"\n}\n\nprint_error() {\n    echo -e \"${RED}âŒ $1${NC}\"\n}\n\n# Check if git is initialized\nif [ ! -d \".git\" ]; then\n    echo \"Initializing Git repository...\"\n    git init\n    print_success \"Git repository initialized\"\nfi\n\n# Check if there's a remote origin\nif ! git remote get-url origin >/dev/null 2>&1; then\n    echo \"\"\n    print_warning \"No GitHub repository configured yet.\"\n    echo \"Please create a GitHub repository first:\"\n    echo \"1. Go to https://github.com/new\"\n    echo \"2. Create a new repository named 'quickqueue'\"\n    echo \"3. Copy the repository URL\"\n    echo \"\"\n    read -p \"Enter your GitHub repository URL: \" repo_url\n    \n    if [ -z \"$repo_url\" ]; then\n        print_error \"Repository URL is required\"\n        exit 1\n    fi\n    \n    git remote add origin \"$repo_url\"\n    print_success \"Remote origin added: $repo_url\"\nelse\n    ORIGIN_URL=$(git remote get-url origin)\n    print_success \"Repository already configured: $ORIGIN_URL\"\nfi\n\n# Add all files to git\necho \"Adding files to git...\"\ngit add .\n\n# Commit changes\necho \"\"\nread -p \"Enter commit message (or press Enter for default): \" commit_message\n\nif [ -z \"$commit_message\" ]; then\n    commit_message=\"feat: initial QuickQueue platform with Docker deployment\"\nfi\n\ngit commit -m \"$commit_message\"\nprint_success \"Changes committed: $commit_message\"\n\n# Push to GitHub\necho \"\"\necho \"Pushing to GitHub...\"\ngit branch -M main\ngit push -u origin main\n\nprint_success \"Successfully pushed to GitHub!\"\necho \"\"\necho \"ðŸŽ‰ QuickQueue is now on GitHub!\"\necho \"Repository: $(git remote get-url origin)\"\necho \"\"\necho \"Next steps:\"\necho \"1. Configure your API keys in deployment\"\necho \"2. Deploy using the Docker deployment guide\"\nprint_success \"Happy coding! ðŸš€\""